mapper.select:
select

from ums_admin
where

  and username = #{username}


  and password=#{password}

mapper.insert:
insert into ums_admin (username, password,
  icon, email, note, 
  create_time, login_time, status
  )
values (#{id,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{icon,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{note,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{loginTime,jdbcType=TIMESTAMP}, #{status,jdbcType=INTEGER}
  )
